<template>
<div>
  <div class="JGcontainer">
    <b-card
      title="Join Game"
      img-src="data:image/jpeg;base64,"
      img-alt="Image"
      img-top
      tag="article"
      style="max-width: 20rem;"
      class="jgCard"
    >
      <b-form inline  @submit.prevent="JoinGame">
        <input
          type="text"
          id="gameid"
          class="form-control"
          placeholder="gameId"
          v-model="gameId"
          required
          autofocus
          style="width: 100px"
          maxlength="10"

        />
        <b-button  type="submit">Join Game</b-button>
      </b-form>
    </b-card>
        <b-card title="Instructions" class="jgCard" >
        <b-card-text>
            Each player will get a randomly generated original word. 
            Odd rounds: Players will have 45 seconds to draw their best representation of the word given. When the countdown gets to zero, the drawing will automatically be submitted and passed to the next player.  
            Even rounds: Players will be shown the drawing that was passed to them. They will have 15 seconds to enter the word that best represents the drawing.  When the countdown gets to zero, the word(s) they guessed will automatically be submitted and passed to the next player.
            The drawing and guess pads will be passed done in the order that the players joined the game.
        </b-card-text>
    </b-card>
    </div>
    <p id="resultMsg"></p>
    <img id="jp" />
  </div>
</template>

<script>
import auth from "../auth";

export default {
  name: "join-the-game",
  data() {
    return {
      gameId: ""
    };
  },

  methods: {
    JoinGame() {
      if (
        this.gameId == null ||
        this.gameId == undefined ||
        this.gameId.length == 0
      ) {
        alert("put in a game id. stop trying to break my shit.");
        return;
      }
      fetch(`${process.env.VUE_APP_GAME_REMOTE_API}/joingame`, {
        method: "POST",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
          Authorization: "Bearer " + auth.getToken()
        },
        credentials: "same-origin",
        body: JSON.stringify(this.gameId)
      })
        .then(response => {
          if (response.ok) {
            this.$emit("gamejoined", this.gameId);
          } else {
            document.getElementById("resultMsg").innerText =
              "You're too late the game has started!";
            document.getElementById("jp").src =
              "https://media.giphy.com/media/5ftsmLIqktHQA/giphy.gif";
          }
        })
        .catch(err => console.error(err));
    }
  }
};
</script>

<style>
.JGcontainer{
  display: flex;
}
.jgCard {
 max-width: 50%;
}
</style>